import React, { useState } from "react";

import "../MenuList/MenuList.scss";

export default function MenuList(props) {
    const [isActive, setActive] = useState(false);

    function changeState() {
        setActive(isActive ? false : true);
    }

    return (
        <ul className={props.class}>
            <li className="navigation__item">
                <a className={"navigation__link ", isActive && "navigation__link--active"} href="#intro" onClick={changeState}>Home</a>
            </li>
            <li className="navigation__item">
                <a className={"navigation__link ", isActive && "navigation__link--active"} href="#products" onClick={changeState}>Products</a>
            </li>
            <li className="navigation__item">
                <a href="#" className={"navigation__link ", isActive && "navigation__link--active"} onClick={changeState}>Faqs</a>
            </li>
            <li className="navigation__item">
                <a href="#" className={"navigation__link ", isActive && "navigation__link--active"} onClick={changeState}>Contact us</a>
            </li>
        </ul>
    )
}
