import React, { useState } from "react";

import "../Faqs/Faqs.scss";

const faqs = [
    {
        title: "What is Cannabidiol (CBD)?",
        text: "Because CBD from hemp has no psychoactive effects, the purchase, sales, or possession of hemp CBD products are completely legal in all 50 States."
    },
    {
        title: "Is Hemp-Derived Cannabidiol (CBD) Legal?",
        text: "Because CBD from hemp has no psychoactive effects, the purchase, sales, or possession of hemp CBD products are completely legal in all 50 States."
    },
    {
        title: "Are there any negative side effects?",
        text: "Because CBD from hemp has no psychoactive effects, the purchase, sales, or possession of hemp CBD products are completely legal in all 50 States."
    },
    {
        title: "Do you offer a return policy?",
        text: "Because CBD from hemp has no psychoactive effects, the purchase, sales, or possession of hemp CBD products are completely legal in all 50 States."
    }
];

const FaqItem = ({ title, text }) => {
    const [isOpen, setState] = useState(false);

    function openItem() {
        setState(isOpen ? false : true);
    }

    return (
        <div className="faqs__item">
            <button className="faqs__button" onClick={openItem}>
                <span className="button-symbol">{isOpen ? "-" : "+"}</span>
                <span className="button-text">{title}</span>
            </button>
            <p className={"faqs-details" + (isOpen ? " faqs-details--open" : "")}>{text}</p>
        </div>
    );
}


export default function Faqs() {
    return (
        <section className="main__faqs faqs" id="faqs">
            <div className="faqs__container">
                <h2 className="faqs__title">Frequently asked questions</h2>
                <div className="faqs__list">
                    {
                        faqs.map(faq => (
                            <FaqItem title={faq.title} text={faq.text} />
                        ))
                    }
                </div>
            </div>
        </section>
    )
}







