import {
    Link
} from "react-router-dom";

import "../MenuList/MenuList.scss";

export default function MenuList(props) {
    let about, intro, products, footer = useRef();

    function changeState(id) {
        const item = ReactDOM.findDOMNode(this.refs[id]);
        console.log(this.refs[id])

        window.scrollTo(item.offsetTop);
    }

    return (
        <ul className={props.class}>
            <li className="navigation__item">
                <a
                    onClick={() => changeState("intro")}
                    // activeClassName="navigation__link--active"
                    className="navigation__link">Home</a>
            </li>
            <li className="navigation__item">
                <a
                    onClick={() => changeState("products")}
                    className="navigation__link"
                    //activeClassName="navigation__link--active"
                    href="#products" ref={products}>Products</a>
            </li>
            <li className="navigation__item">
                <a href="#faqs"
                    onClick={() => changeState("faqs")}
                    //activeClassName="navigation__link--active"
                    className="navigation__link"
                >Faqs</a>
            </li >
            <li className="navigation__item">
                onClick={() => changeState("footer")}
                <a href="#footer"
                    //activeClassName="navigation__link--active"
                    className="navigation__link"
                >Contact us</a>
            </li >
        </ul >
