import React, { ReactDOM, useEffect, useRef, useState } from "react";
import {
    Link
} from "react-router-dom";

import "../MenuList/MenuList.scss";

export default function MenuList(props) {
    const faqs = useRef(null),
        intro = useRef(null),
        products = useRef(null),
        footer = useRef(null);
    const [active, setActive] = useState();

    function changeState() {

    }

    useEffect(() => {
        if (faqs.current === active) {
            faqs.current.className = "navigation__link navigation__link--active";
        }

        if (products.current === active) {
            products.current.className = "navigation__link navigation__link--active";
        }

        if (footer.current === active) {
            footer.current.className = "navigation__link navigation__link--active";
        }

        if (intro.current === active) {
            intro.current.className = "navigation__link navigation__link--active";
        }

    });

    return (
        <ul className={props.class}>
            <li className="navigation__item">
                <a
                    href="#intro"
                    ref={el => { intro.current = el; setActive(el) }}
                    className="navigation__link">Home</a>
            </li>
            <li className="navigation__item">
                <a
                    className="navigation__link"
                    href="#products" ref={el => { products.current = el; setActive(el) }}>Products</a>
            </li>
            <li className="navigation__item">
                <a href="#faqs"
                    ref={el => { faqs.current = el; setActive(el) }}
                    className="navigation__link"
                >Faqs</a>
            </li >
            <li className="navigation__item">
                <a href="#footer"
                    ref={el => { footer.current = el; setActive(el) }}
                    className="navigation__link"
                >Contact us</a>
            </li >
        </ul >
    )
}
