import React, { useEffect, useState } from "react";
import { Storage } from "../Products/Products";

export default function Cart() {
    const [count, setCount] = useState(0);

    if (new Storage().count) useState(new Storage().count);

    return (
        <div className="func-block__item ">
            <span className="cart__count">Cart({count})</span>
        </div>
    );
}
