import React from "react";
import Slider from "react-slick";

import "../FeedBackSlider/FeedBackSlider.scss";

export default function FeedBackSlider(props) {

    const settings = {
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        className: props.class,
        nextArrow: <button className="slick-arrow slick-arrow--right"></button>,
        prevArrow: <button className="slick-arrow slick-arrow--left"></button>
    };

    return (
        <Slider {...settings}>
            {props.children}
        </Slider>
    );
}
