import React from "react";
import { Star } from "../../common/Svg/Svg";
import { goods } from "../../mockData/goods";
import Button from "../../common/Button/Button";


import "../Products/Products.scss";

export class Storage {
    constructor() {
        this.count = 0;
        localStorage.setItem("count", 0);
    }
    getCount() {
        this.count = +localStorage.getItem("count");
    }

    static add() {
        localStorage.setItem("count", this.count + 1);
    }
}





const Product = ({ rating, title, src, price, key, get }) => {
    localStorage.setItem("count", 0);

    function addToCart() {
        Storage.add();
        get(new Storage().count);
    }

    return (<div className="products-slider__item product" key={key} >
        <div className="product__top">
            <div className="product__rating">
                {rating.map(mark => mark)}
            </div>
            <div className="product__title">{title}</div>
        </div>
        <div className="product__image">
            <img src={src} alt="product" className="product-image" />
        </div>
        <div className="product__buy">
            <span className="product__price">${price} USD</span>
            <Button class="button--buy" onclick={addToCart}>
                <span className="button-text">Shop</span>
                <span className="button-line"></span>
            </Button>
        </div>
    </div>
    );
}

export default function Products(props) {
    function getRating(rating) {
        const stars = new Array(5);
        stars.fill(<Star className="star-svg" />, 0, rating);
        return stars;
    }

    function getCount(count) {
        props.get(count);

        console.log("products-count", count);
    }

    function getProductsList() {

        return goods.map((good, index) => (
            <Product
                rating={getRating(good.rating)}
                title={good.title}
                src={good.image}
                price={good.price}
                key={index} get={getCount} />
        ));
    }

    return (
        <section className="main__products products" id="products" >
            <h2 className="products__title">CBD Featured Products</h2>
            <section className="products__slider products-slider">
                {getProductsList()}
                <div className="slider-arrow">
                    <div className="arrow"></div>
                    <div className="arrow"></div>
                </div>
            </section>
        </section >
    )
}
