import React, { useEffect, useState } from "react";

export default function Cart() {
    const [count, setCount] = useState(0);

    function getCount() {
        setCount(localStorage.getItem("count").length);
    }

    useEffect(() => (
        getCount()
    ));

    return (
        <div className="func-block__item ">
            <span className="cart__count">Cart({count})</span>
        </div>
    );
}
// {count}
