import React, { ReactDOM, useEffect, useRef, useState } from "react";
import {
    Link
} from "react-router-dom";

import "../MenuList/MenuList.scss";

export default function MenuList(props) {
    let about, intro, products, footer = useRef();
    const [active, setActive] = useState("");

    function changeState() {
        setActive("navigation__link--active");
        // if (products.current) {
        //     products.current.className += "navigation__link--active";
        // }
    }

    useEffect(() => {
        if (products.current) {
            products.current.className += "navigation__link--active";
        }
        if (about.current) {
            about.current.className += "navigation__link--active";
        }

        if (products.current) {
            products.current.className += "navigation__link--active";
        }
        if (about.current) {
            about.current.className += "navigation__link--active";
        }
        console.log(products, about, intro, footer)
    });

    return (
        <ul className={props.class}>
            <li className="navigation__item">
                <a
                    // activeClassName="navigation__link--active"
                    className="navigation__link">Home</a>
            </li>
            <li className="navigation__item">
                <a
                    className="navigation__link"
                    onClick={changeState}
                    //activeClassName="navigation__link--active"
                    href="#products" ref={products}>Products</a>
            </li>
            <li className="navigation__item">
                <a href="#faqs"
                    //activeClassName="navigation__link--active"
                    className="navigation__link"
                >Faqs</a>
            </li >
            <li className="navigation__item">

                <a href="#footer"
                    //activeClassName="navigation__link--active"
                    className="navigation__link"
                >Contact us</a>
            </li >
        </ul >
        // <ul className={props.class}>
        //     <li className="navigation__item">
        //         <a
        //             className={"navigation__link " + (isActive && "navigation__link--active")}
        //             href="#intro" >Home</a>
        //     </li>
        //     <li className="navigation__item">
        //         <a
        //             className={"navigation__link " + (isActive && "navigation__link--active")}
        //             href="#products" >Products</a>
        //     </li>
        //     <li className="navigation__item">
        //         <a href="#"
        //             className={"navigation__link " + (isActive && "navigation__link--active")}
        //             >Faqs</a>
        //     </li>
        //     <li className="navigation__item">
        //         <a href="#"
        //             className={"navigation__link " + (isActive && "navigation__link--active")}
        //             >Contact us</a>
        //     </li>
        // </ul>
    )
}
