import React, { useState } from "react";

import {
    NavLink
} from "react-router-dom";

import "../MenuList/MenuList.scss";

export default function MenuList(props) {
    const [isActive, setActive] = useState(false);

    function changeState() {
        setActive(isActive ? false : true);
    }

    return (
        <ul className={props.class}>
            <li className="navigation__item">
                <NavLink
                    className={"navigation__link " + (isActive && "navigation__link--active")}
                    href="#intro" onClick={changeState}>Home</NavLink>
            </li>
            <li className="navigation__item">
                <NavLink
                    className={"navigation__link " + (isActive && "navigation__link--active")}
                    href="#products" onClick={changeState}>Products</NavLink>
            </li>
            <li className="navigation__item">
                <NavLink to="#faqs" activeClassName="navigation__link--active"
                    className="navigation__link"
                    onClick={changeState}>Faqs</NavLink>
            </li>
            <li className="navigation__item">
                <NavLink href="#footer"
                    activeClassName="navigation__link--active"
                    className="navigation__link"
                    onClick={changeState}>Contact us</NavLink>
            </li>
        </ul>
