import React from "react";
import { Star } from "../../common/Svg/Svg";
import { goods } from "../../mockData/goods";

import "../Products/Products.scss";

export const Product = ({ rating, title, src, price }) => (
    <div className="products-slider__item product">
        <div className="product__top">
            {rating}
            <div className="product__title">{title}</div>
        </div>
        <div className="product__image">
            <img src={src} alt="product" className="product-image" />
        </div>
        <div className="product__buy">
            <span className="product__price">${price} USD</span>
            <button className="button button--buy">
                <span className="button-text">Shop</span>
                <span className="button-line"></span>
            </button>
        </div>
    </div>
);


export default function Products() {
    function getRating(rating) {
        const stars = new Array(5);
        stars.fill(<Star className="star-svg" />, 0, rating);
        return stars.map(star => star);
    }

    function getProductsList() {
        console.log(goods[0].rating);

        return goods.map(good => (
            <Product
                rating={getRating(good.rating)}
                title={good.title}
                src={good.image}
                price={good.price} />
        ));
    }

    return (
        <section className="main__products products" id="products" >
            <h2 className="products__title">CBD Featured Products</h2>
            <section className="products__slider products-slider">
                {getProductsList}
                <div className="slider-arrow">
                    <div className="arrow"></div>
                    <div className="arrow"></div>
                </div>
            </section>
        </section >
    )
}



